import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/Math.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/cryptography/MerkleProof.sol";
import "../interfaces/ITokenDistributor.sol";

contract TokenDistributor is ITokenDistributor, Ownable {
    using SafeMath for uint256;

    /// @inheritdoc ITokenDistributor
    mapping(address => bytes32) public override merkleRoots;

    /// @inheritdoc ITokenDistributor
    mapping(address => mapping(address => uint256))
        public
        override claimedRewards;

    constructor() {}

    /* ========== ONLY OWNER ========== */

    /// @inheritdoc ITokenDistributor
    function updateMerkleRoot(address rewardToken, bytes32 merkleRoot)
        external
        override
        onlyOwner
    {
        merkleRoots[rewardToken] = merkleRoot;
        emit UpdateMerkleRoot(msg.sender, rewardToken, merkleRoot);
    }

    function emergencyWithdraw(address token, address to) external onlyOwner {
        require(merkleRoots[token] == bytes32(0), "Update to Zero First!");
        uint256 bal = IERC20(token).balanceOf(address(this));
        if (bal > 0) {
            IERC20(token).transfer(to, bal);
        }
    }

    /* ========== EXTERNAL ========== */

    /// @inheritdoc ITokenDistributor
    function claim(
        address rewardToken,
        address account,
        uint256 totalAmount,
        bytes32[] calldata merkleProof
    ) external override {
        // Check Root
        bytes32 merkleRoot = merkleRoots[rewardToken];
        require(
            merkleRoot != bytes32(0),
            "No MerkleRoot For this Reward Token!"
        );

        // Verify the merkle proof.
        bytes32 node = keccak256(abi.encodePacked(account, totalAmount));
        require(
            MerkleProof.verify(merkleProof, merkleRoot, node),
            "MerkleDistributor: Invalid proof."
        );

        // Calculate claimable amount
        uint256 collected = claimedRewards[rewardToken][account];
        uint256 claimable = Math.min(
            totalAmount.sub(collected),
            IERC20(rewardToken).balanceOf(address(this))
        );

        // send the token
        if (claimable > 0) {
            claimedRewards[rewardToken][account] = collected.add(claimable);
            IERC20(rewardToken).transfer(account, claimable);
        }

        // EVENT
        emit ClaimRewards(rewardToken, account, claimable);
    }
}
