// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.7.6;

import "../interfaces/UNTERC20.sol";

contract UToken is UNTERC20 {
    address private _owner;

    constructor(
        address ownerAddress,
        string memory _name,
        string memory _symbol,
        uint8 _decimals
    ) UNTERC20(_name, _symbol, _decimals) {
        _owner = ownerAddress;
    }

    modifier onlyOwner() {
        require(msg.sender == _owner, "Only Owner!");
        _;
    }

    function mint(address account, uint256 amount) external onlyOwner {
        _mint(account, amount);
        emit Mint(msg.sender, account, amount);
    }

    function burn(address account, uint256 value) external onlyOwner {
        _burn(account, value);
        emit Burn(msg.sender, account, value);
    }

    event Mint(address sender, address account, uint256 amount);
    event Burn(address sender, address account, uint256 amount);
}
